import styled from "styled-components";

const StyledTags = styled.div`
  padding: 1rem;
  margin-bottom: 2rem;

  h2 {
    display: inline;
    font-size: 2.5rem;
    padding: 0.5rem 3rem;
  }

  h3 {
    font-size: 1.5rem;
  }

  h3:last-of-type {
    margin-top: 1rem;
  }
`;

export default StyledTags;
